import AuthenticationService from '@/services/AuthenticationService';

import * as types from './mutation-types';

const authenticationService = new AuthenticationService();

export const registerUser = ({ commit }, userData) => new Promise((resolve, reject) => {
  authenticationService
    .registerUser(userData)
    .then((response) => {
      commit(types.SET_SIGNUP_SUCCESS, response);
      resolve(response);
    })
    .catch((error) => {
      commit(types.SET_SIGNUP_ERRORS, error.response.data);
      reject(error);
    });
});

export const userLogin = ({ commit }, userCredentials) => new Promise((resolve, reject) => {
  authenticationService
    .login(userCredentials)
    .then((success) => {
      commit(types.SET_LOGIN_SUCCESS, success.data.status);
      resolve(success);
    })
    .catch((error) => {
      commit(types.SET_LOGIN_ERRORS, error.response.data);
      reject(error);
    });
});

export const emailVerification = ({ commit }, emailData) => new Promise((resolve, reject) => {
  authenticationService
    .emailVerification(emailData)
    .then((success) => {
      commit(types.SET_EMAIL_VERIFICATION_SUCCESS, success.data.status);
      resolve(success);
    })
    .catch((error) => {
      commit(types.SET_EMAIL_VERIFICATION_ERRORS, error.response.data);
      reject(error);
    });
});
