import BaseService from './BaseService';


export default class TagService extends BaseService {
    tagList(param) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}tag?page=${param}`;
          this.http.get(apiEndpoint,{
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              Authorization: `Token ${this.getUserToken()}`,
            },
          }).then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }

      tagCreate(tagData) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}tag/`;
          this.http.post(apiEndpoint,tagData,{
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              Authorization: `Token  ${this.getUserToken()}`,
            },
          }).then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }

      tagDetails(tagId) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}post/${postId}`;
          this.http.get(apiEndpoint,{
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              Authorization: `Token  ${this.getUserToken()}`,
            },
          }).then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }

      postUpdate(postData) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}post/${postData.id}`;
          this.http.put(apiEndpoint,postData,{
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              Authorization: `Token  ${this.getUserToken()}`,
            },
          }).then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }

      tagDelete(tagId) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}tag/${tagId}`;
          this.http.delete(apiEndpoint,{
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              Authorization: `Token ${this.getUserToken()}`,
            },
          }).then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }
}
