import BaseService from './BaseService';

export default class PostService extends BaseService {

    postList() {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}post`;
          this.http.get(apiEndpoint,{
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              Authorization: `Token  ${this.getUserToken()}`,
            },
          }).then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }

      postCreate(postData) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}post`;
          this.http.post(apiEndpoint,postData,{
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              Authorization: `Token  ${this.getUserToken()}`,
            },
          }).then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }

      postDetails(postId) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}post/${postId}`;
