import PostService from '@/services/PostService';

import * as types from './mutation-types';

const postService = new PostService();

export const postList = ({ commit }) => new Promise((resolve, reject) => {
  postService
    .postList()
    .then((response) => {
      commit(types.SET_POST_LIST, response);
      resolve(response);
    })
    .catch((error) => {
      reject(error);
    });
});


export const postCreate = ({ commit },postData) => new Promise((resolve, reject) => {
  postService
    .postCreate(postData)
    .then((response) => {
      commit(types.SET_POST_SUCCESS, response);
      resolve(response);
    })
    .catch((error) => {
      commit(types.SET_POST_ERROR, error.response.data);
      reject(error);
    });
});



export const postDetails = ({ commit },postId) => new Promise((resolve, reject) => {
  postService
    .postDetails(postId)
    .then((response) => {
      commit(types.SET_POST_DETAILS, response);
      resolve(response);
    })
    .catch((error) => {
      reject(error);
    });
});
export const postUpdate = ({ commit },postData) => new Promise((resolve, reject) => {
  postService
    .postUpdate(postData)
    .then((response) => {
      commit(types.SET_POST_SUCCESS, response);
      resolve(response);
    })
    .catch((error) => {
      commit(types.SET_POST_ERROR, error.response.data);
      reject(error);
    });
});



export const postDelete = ({ commit },postId) => new Promise((resolve, reject) => {
  postService
    .postDelete(postId)
    .then((response) => {
      commit(types.SET_POST_SUCCESS, response);
      resolve(response);
    })
    .catch((error) => {
      commit(types.SET_POST_ERROR, error.response.data);
      reject(error);
    });
});
