import BaseService from './BaseService';

export default class AuthenticationService extends BaseService {

    registerUser(data) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}auth/register`;
          this.http.post(apiEndpoint, data)
            .then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }

      loginUser(data) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}auth/login`;
          this.http.post(apiEndpoint, data)
            .then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }
      logoutUser(data) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}auth/logout`;
          this.http.post(apiEndpoint, data,  this.getHeaders({
            headers: {
              Authorization: `Token ${this.getUserToken()}`,
            }
            }))
            .then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }
    static getHeaders(toAppend = {}) {
        return {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          ...toAppend,
        };
      }
}
