import BaseService from './BaseService';

export default class AuthenticationService extends BaseService {

    registerUser(userData) {
        return new Promise((resolve, reject) => {
          const apiEndpoint = `${this.baseURL}auth/register`;
          this.http.post(apiEndpoint, userData)
            .then((response) => {
              resolve(response);
            })
            .catch((error) => {
              reject(error);
            });
        });
      }

    static loginUser(data){
        let dataURL = `${this.serverURL}auth/login`;
        return axios.post(dataURL, data, {
            headers: {

                'Content-Type': 'application/json',

            }});
    }
    static logoutUser(data){
        let dataURL = `${this.serverURL}auth/logout`;
        return axios.post(dataURL, data, {
            headers: {
                'Authorization':'Token '+localStorage.getItem('token'),
                'Content-Type': 'application/json',


            }});
    }

    static getHeaders(toAppend = {}) {
        return {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          ...toAppend,
        };
      }
}
