import TagService from '@/services/TagService';
import * as types from './mutation-types';

const tagService = new TagService();

export const tagList = ({ commit }, param) => new Promise((resolve, reject) => {
    tagService
    .tagList(param)
    .then((response) => {
      commit(types.SET_TAG_LIST, response);
      resolve(response);
    })
    .catch((error) => {
      reject(error);
    });
});

export const tagDelete = ({ commit },postId) => new Promise((resolve, reject) => {
    postService
      .postDelete(postId)
      .then((response) => {
        commit(types.SET_POST_SUCCESS, response);
        resolve(response);
      })
      .catch((error) => {
        commit(types.SET_POST_ERROR, error.response.data);
        reject(error);
      });
  });
